// client/src/context/AuthContext.js
import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

// Create context
export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')) || null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    // Set up axios defaults
    axios.defaults.baseURL = 'http://localhost:5000';

    // Set user header for all requests if available
    useEffect(() => {
        if (user) {
            axios.defaults.headers.common['x-username'] = user.username;
        } else {
            delete axios.defaults.headers.common['x-username'];
        }
    }, [user]);

    // Register user
    const register = async (userData) => {
        try {
            setError(null);
            const res = await axios.post('/api/users/register', userData);

            // Set user
            localStorage.setItem('user', JSON.stringify(res.data.user));
            setUser(res.data.user);

            return true;
        } catch (err) {
            setError(err.response?.data?.message || 'An error occurred during registration');
            return false;
        }
    };

    // Login user
    const login = async (userData) => {
        try {
            setError(null);
            const res = await axios.post('/api/users/login', userData);

            // Set user
            localStorage.setItem('user', JSON.stringify(res.data.user));
            setUser(res.data.user);

            return true;
        } catch (err) {
            setError(err.response?.data?.message || 'Invalid credentials');
            return false;
        }
    };

    // Logout user
    const logout = () => {
        // Remove user from localStorage
        localStorage.removeItem('user');

        // Clear state
        setUser(null);
        setError(null);
    };

    // Rate a song
    const rateSong = async (songId, rating) => {
        try {
            await axios.post('/api/users/rate', { songId, rating });
            return true;
        } catch (err) {
            console.error('Error rating song:', err.response?.data?.message || err.message);
            return false;
        }
    };

    // Add a song to favorites
    const addToFavorites = async (songId) => {
        try {
            await axios.post('/api/users/favorites', { songId });

            // Update user profile to reflect changes
            const res = await axios.get('/api/users/profile');

            // Only update favorites in the local user state, not overwriting the whole user
            setUser(prevUser => ({
                ...prevUser,
                favorites: res.data.favorites
            }));

            return true;
        } catch (err) {
            console.error('Error adding to favorites:', err.response?.data?.message || err.message);
            return false;
        }
    };

    // Clear any errors
    const clearError = () => setError(null);

    return (
        <AuthContext.Provider
            value={{
                user,
                loading,
                error,
                register,
                login,
                logout,
                rateSong,
                addToFavorites,
                clearError
            }}
        >
            {children}
        </AuthContext.Provider>
    );
};
